	$: iconContent = icon ? getIconContent(icon) : null;
	$: isIconEmoji = icon ? isEmoji(icon) : false;
	$: iconSVGString = icon && iconSVGs[icon] ? iconSVGs[icon] : null;
	$: isIconSVG = !!iconSVGString;
	
	function getIconContent(iconStr: string): string | null {
		if (!iconStr) return null;
		
		// Check if it's an emoji
		if (isEmoji(iconStr)) {
			return iconStr;
		}
		
		// Check if it's a known icon name with SVG
		if (iconSVGs[iconStr]) {
			return iconSVGs[iconStr];
		}
		
		// If it's in the available icons list but no SVG, return null for text fallback
		if (AVAILABLE_ICON_NAMES.includes(iconStr as any)) {
			return null;
		}
		
		// Otherwise, treat as custom text/emoji
		return iconStr;
	}
</script>

{#if icon}
	{#if isIconEmoji}
		<span class="text-2xl">{icon}</span>
	{:else if iconSVGString}
		<span class="w-full h-full flex items-center justify-center">
			{@html iconSVGString || ''}
		</span>
	{:else if iconContent}
		<span class="text-sm">{iconContent}</span>
	{:else}
		<span class="text-sm text-gray-400">{icon}</span>
	{/if}
{/if}
